import React, { useState } from "react";
import NoteContainer from "./Components/NoteContainer/NoteContainer";
import "./App.css";
import "./Components/Note/Note.css";
import Sidebar from "./Components/Sidebar/Sidebar";
function App() {
  const [notes, setNotes] = useState([
    {
      text: "gaksudg",
      time: "3:12AM",
      color: "cyan",
    },
    {
      text: "gaksudg",
      time: "3:12AM",
      color: "yellow",
    },
    {
      text: "gaksudg",
      time: "3:12AM",
      color: "blue",
    },
    {
      text: "gaksudg",
      time: "3:12AM",
      color: "green",
    },
    {
      text: "gaksudg",
      time: "3:12AM",
      color: "gray",
    },
  ]);

  const addNote = (color) => {
    const tempNotes = [...notes];
    tempNotes.push({
      text: "",
      time: Date.now(),
      color,
    });
    setNotes(tempNotes);
  };

  return (
    <div className="App">
      <Sidebar addNote={addNote} />
      <NoteContainer notes={notes} />
    </div>
  );
}

export default App;
